mapper.select:
		select * from product where ${search_type} like '%'||#{keyword}||'%'
		
        AND MAJORCATEGORY = #{majorcategory}
   		
   		
   			
   				AND PRICE <= #{product_price2}
   			
   				AND PRICE >= #{product_price1}
   			
   				AND PRICE <= #{product_price2} AND PRICE >= #{product_price1}
   			
   		
   		
   			order by ${order} ${order_by}
